$(function(){$('a[href^="#"]').on("click",function(a){a.preventDefault();var i=$(this).attr("href"),o=$(i).offset().top;$("html, body").animate({scrollTop:o-70},1e3)})});var audio1=$("#sound1")[0],audio2=$("#sound2")[0],audio3=$("#sound3")[0];$("#play1").click(function(){If(audio1.paused),audio1.play()}),$("#pause1").click(function(){audio1.pause()}),$("#play2").click(function(){audio2.play()}).click(function(){audio2.pause()}),$("#play3").click(function(){audio3.play()}).click(function(){audio3.pause()});